CREATE TABLE shop (  shopID                                VARCHAR(37)  NOT NULL  COMMENT 'ID主键',  shopName                              VARCHAR(32)  NOT NULL  COMMENT '名称',  deleteFlag                            TINYINT      NOT NULL  COMMENT '删除标记',  description                           VARCHAR(256) NOT NULL  COMMENT '描述',  config_probe_visit_expired            INT(11)      NOT NULL  COMMENT '访问过期时间段（分钟）',  config_probe_user_expired             INT(11)      NOT NULL  COMMENT '用户过期时间段（天）',  config_probe_visit_signal             INT(11)      NOT NULL  COMMENT '信号强度过滤（0 - 100）',  config_probe_visit_expired_wifi       INT(11)      NOT NULL  COMMENT 'WIFI 访客过期时间',  config_probe_visit_signal_wifi        INT(11)      NOT NULL  COMMENT 'WIFI 信号过滤',  config_probe_api_visit_counts         VARCHAR(64)  NOT NULL  COMMENT '到访次数统计规则（1, 2, 5）次数',  config_probe_api_visit_duration       VARCHAR(64)  NOT NULL  COMMENT '到访时长统计规则（5, 30, 60, 120）分钟',  config_probe_api_visit_duration_deep  FLOAT(11, 0) NOT NULL  COMMENT '到访时长达到深访的标准（分钟）',  config_probe_api_visit_duration_jump  FLOAT(11, 0) NOT NULL  COMMENT '到访时长达到跳出的标准（分钟）',  config_probe_api_visit_duration_enter FLOAT(11, 0) NOT NULL  COMMENT '到访时长达到入店的标准（分钟）',  config_probe_api_visit_period         VARCHAR(64)  NOT NULL  COMMENT '到访周期统计规则（1, 2, 4, 7, 14）天',  config_probe_api_liveness             VARCHAR(64)  NOT NULL  COMMENT '活跃度统计(1, 7, 15, 30天)',  createTime                            DATETIME     NOT NULL  COMMENT '创建时间',  PRIMARY KEY (id));CREATE TABLE probe_binding(  probeID    VARCHAR(37) PRIMARY KEY NOT NULL  COMMENT 'ID主键',  shopID     VARCHAR(37) COMMENT '店铺ID',  createTime DATETIME COMMENT '创建时间',  updateTime DATETIME COMMENT '更新时间',  FOREIGN KEY (shopID) REFERENCES shop (shopID));CREATE TABLE probe_log (  id              VARCHAR(37) NOT NULL  COMMENT 'ID主键',  idshop          VARCHAR(20) NOT NULL  COMMENT '店铺ID',  idwifi          VARCHAR(20) NOT NULL  COMMENT 'wifi ID',  idvisit         VARCHAR(37) NOT NULL  COMMENT '访问ID',  idvisitwifi     VARCHAR(37) NOT NULL  COMMENT 'WIFI访问ID',  signal_strength INT(11)     NOT NULL  COMMENT 'WiFi信号强弱',  mac_device      VARCHAR(48) NOT NULL  COMMENT '手机Mac',  mac_wifi        VARCHAR(48) NULL DEFAULT NULL  COMMENT 'wifi mac地址',  ssid            VARCHAR(32) NULL DEFAULT NULL  COMMENT 'wifi的SSID号',  local_time      DATETIME    NOT NULL  COMMENT '本地时间',  create_time     DATETIME    NOT NULL  COMMENT '创建时间',  update_time     DATETIME    NOT NULL  COMMENT '更新时间',  PRIMARY KEY (id),  INDEX fk_t_probe_log_t_shop_1 (idshop),  INDEX fk_t_probe_log_t_wifi_1 (idwifi),  INDEX fk_t_probe_log_t_visit_1 (idvisit),  INDEX fk_t_probe_log_t_visit_wifi_1 (idvisitwifi));CREATE TABLE t_visit (  id             VARCHAR(37) NOT NULL  COMMENT 'ID主键',  idshop         VARCHAR(20) NOT NULL  COMMENT '店铺ID',  idwifi         VARCHAR(20) NOT NULL  COMMENT 'wifi ID',  mac_device     VARCHAR(48) NOT NULL  COMMENT '设备Mac',  time_entry     DATETIME    NOT NULL  COMMENT '进入时间',  time_leave     DATETIME    NOT NULL  COMMENT '离开时间',  time_duration  INT(11)     NOT NULL  COMMENT '驻店时间',  time_from_last INT(11)     NOT NULL  COMMENT '上次访问到现在的时间差',  is_new_user    TINYINT(1)  NOT NULL  COMMENT '是否新用户',  count_logs     INT(11)     NOT NULL  COMMENT '日志条数',  end_brand      VARCHAR(16) NOT NULL  COMMENT '设备品牌',  create_time    DATETIME    NOT NULL  COMMENT '创建时间',  update_time    DATETIME    NOT NULL  COMMENT '更新时间',  PRIMARY KEY (id),  INDEX fk_t_customer_t_shop_1 (idshop),  INDEX fk_t_customer_t_wifi_1 (idwifi));CREATE TABLE t_visit_wifi (  id            VARCHAR(37) NOT NULL  COMMENT 'ID主键',  idshop        VARCHAR(20) NOT NULL  COMMENT '店铺ID',  idwifi        VARCHAR(20) NOT NULL  COMMENT 'wifi ID',  mac_device    VARCHAR(48) NOT NULL  COMMENT '设备Mac',  time_entry    DATETIME    NOT NULL  COMMENT '进入时间',  time_leave    DATETIME    NOT NULL  COMMENT '离开时间',  time_duration INT(11)     NOT NULL  COMMENT '停留时间',  count_logs    INT(11)     NOT NULL  COMMENT '日志条数',  create_time   DATETIME    NOT NULL  COMMENT '创建时间',  update_time   DATETIME    NOT NULL  COMMENT '更新时间',  PRIMARY KEY (id),  INDEX fk_t_customer_t_shop_1 (idshop),  INDEX fk_t_customer_t_wifi_1 (idwifi));CREATE TABLE mac_brand (  mac    VARCHAR(7)  NOT NULL  COMMENT 'ID主键',  name   VARCHAR(64) NOT NULL  COMMENT '店铺ID',  remark VARCHAR(64) NOT NULL  COMMENT 'wifi ID',  PRIMARY KEY (mac));